code.installcode:
$queries = array();
$queries[] = sprintf('CREATE TABLE `%sraid_guilds` (`id` SMALLINT UNSIGNED NOT NULL AUTO_INCREMENT PRIMARY KEY, `name` VARCHAR(64) NOT NULL) ENGINE = MyISAM', TABLE_PREFIX);
$queries[] = sprintf('CREATE TABLE `%sraid_roster` (`uid` BIGINT UNSIGNED NOT NULL, UNIQUE (`uid`)) ENGINE = MyISAM', TABLE_PREFIX);

foreach ($queries as $query)
    $db->query_write($query);

code.uninstallcode:
$queries = array();
$queries[] = sprintf('DROP TABLE IF EXISTS `%sraid_guilds`', TABLE_PREFIX);
$queries[] = sprintf('DROP TABLE IF EXISTS `%sraid_roster`', TABLE_PREFIX);

foreach ($queries as $query)
    $db->query_write($query);

